from flask import Flask, request
app = Flask(__name__)

@app.route("/evalcode")
def eval_code():
    user_code = request.args.get('code', '')
    try:
        compiled_code = compile(user_code, '<string>', 'exec')
        namespace = {}
        exec(compiled_code, namespace)
        result = namespace.get('result', '')
        return str(result)
    except Exception as e:
        return str(e)
