def getData(dataStart, dataLimit, thread, lock):
    while True:
        lock.acquire()
        if dataStart[0] <= dataLimit:
            print(f"Thread-{thread} is accessing data: {dataStart[0]}")
            dataStart[0] += 1
            lock.release()
            time.sleep(0.1)
        else:
            lock.release()
            break
